/* ==== LICENSED BLOGGER THEME | DO NOT REMOVE ==== */
(function(){
  var checkLicense = function(){
    try {
      /* ===============================
         üîê ENCRYPTED LICENSE DATA
         =============================== */
      var _d = atob("ODY0NzYyOTY5ODUxMzU4NTM4Mw=="); // Blog-ID
      var _x = atob("cXV3YXRlcWF1bS5jb20=");        // quwateqaum.com
      var _y = atob("ZGFpbHl1cGRhdGVraGFsaWQuYmxvZ3Nwb3QuY29t"); // blogspot
      var _e = atob("MjAyNy0xMi0zMQ==");            // Expiry: 2027-12-31
      var _v = atob("aHR0cHM6Ly9yYXcuZ2l0aHVidXNlcmNvbnRlbnQuY29tL21zaW05L2FobWhvc3QtbGljZW5zZS9tYWluL2xpY2Vuc2UuanNvbg==");

      var expiry = new Date(_e);
      var now = new Date();
      var domain = location.hostname.replace(/^www\./,"");
      var html = document.documentElement.innerHTML;

      /* ===============================
         ‚è≥ EXPIRY CHECK
         =============================== */
      if (now > expiry) throw "LICENSE_EXPIRED";

      /* ===============================
         üîë BLOG-ID CHECK
         =============================== */
      // Checking if Blog ID exists in the rendered HTML
      if (html.indexOf(_d) === -1) throw "INVALID_BLOG";

      /* ===============================
         üåê DOMAIN CHECK
         =============================== */
      if (![ _x, _y ].includes(domain)) throw "INVALID_DOMAIN";

      /* ===============================
         üåç ONLINE LICENSE VERIFY
         =============================== */
      // Add timestamp to bypass GitHub CDN cache
      fetch(_v + "?t=" + new Date().getTime(), {cache:"no-store"})
        .then(r=>r.json())
        .then(j=>{
          if (!j.active || j.blogId !== _d) {
            // Async Block
            throw "REMOTE_REVOKED";
          }
        })
        .catch((e)=>{ 
           // Handle remote failure by blocking page
           if(e === "REMOTE_REVOKED") showBlockError();
        });

      /* ===============================
         üîÅ FOOTER CREDIT AUTO-RESTORE
         =============================== */
      var f = document.getElementById("theme-credit");
      if(!f){
        var c=document.createElement("div");
        c.id="theme-credit";
        c.style.cssText="text-align:center;font-size:12px;color:#888;margin:20px 0";
        c.innerHTML='Theme by <a href="#" rel="nofollow">Licensed Developer</a>';
        document.body.appendChild(c);
      }

      /* ===============================
         üìß EXPIRY WARNING (30 DAYS)
         =============================== */
      var daysLeft = Math.ceil((expiry-now)/(1000*60*60*24));
      if (daysLeft <= 30){
        console.warn("‚ö† Theme license expires in "+daysLeft+" days");
        var w=document.createElement("div");
        w.style.cssText="background:#fff3cd;color:#856404;padding:12px;text-align:center;font-size:14px";
        w.innerHTML="‚ö† Theme license expires in <b>"+daysLeft+" days</b>. Please renew.";
        document.body.prepend(w);
      }

    } catch(e) {
      console.error("License Error:", e);
      showBlockError();
    }
  };

  function showBlockError(){
    document.documentElement.innerHTML = `
      <style>
        body{font-family:Arial,sans-serif;background:#fff;text-align:center;padding:100px 20px;}
        h1{color:#c00;} p{font-size:16px;}
      </style>
      <h1>Theme License Error</h1>
      <p>This Blogger theme is not licensed for this website.</p>
      <p>Please contact the theme developer.</p>
    `;
  }

  // Defer execution to ensure HTML is fully loaded for ID check
  if(document.readyState === "complete" || document.readyState === "interactive") {
    checkLicense();
  } else {
    document.addEventListener("DOMContentLoaded", checkLicense);
  }
})();
