//
function resizeText(multiplier) {
    var elem = document.getElementById("post-body-content");
    var style = window.getComputedStyle(elem, null).getPropertyValue('font-size');
    var fontSize = parseFloat(style); 
    elem.style.fontSize = (fontSize + multiplier) + 'px';
}

// Fetch Related Posts
document.addEventListener("DOMContentLoaded", function() {
    var labelElem = document.getElementById('post-label-data');
    if (labelElem) {
        var label = labelElem.textContent.trim();
        fetch('/feeds/posts/summary/-/' + label + '?alt=json&max-results=4')
            .then(res => res.json())
            .then(data => {
                var entries = data.feed.entry;
                var container = document.getElementById('related-posts-grid');
                if (!entries || entries.length <= 1) { // 1 because current post might be included
                    container.innerHTML = 'No related posts found.';
                    return;
                }

                var html = '';
                var count = 0;
                // Filter out current post if possible, but title check is simple
                var currentTitle = document.querySelector('.post-title').textContent.trim();

                entries.forEach(entry => {
                    if (count >= 3) return; // Limit to 3
                    if (entry.title.$t === currentTitle) return; // Skip current

                    var title = entry.title.$t;
                    var link = entry.link.find(l => l.rel === 'alternate').href;
                    var img = 'https://via.placeholder.com/300x200';
                    if (entry.media$thumbnail) {
                        img = entry.media$thumbnail.url.replace(/\/s[0-9]+-c/, "/w300-h200-c"); 
                    }

                    html += `
                        <div class="related-item">
                            <a href="${link}">
                                <img src="${img}" class="related-thumb" alt="${title}">
                                <h4>${title}</h4>
                            </a>
                        </div>
                    `;
                    count++;
                });
                container.innerHTML = html;
            })
            .catch(err => console.log('Related posts error', err));
    }
});
