//
document.addEventListener("DOMContentLoaded", function() {
    const widgets = document.querySelectorAll('.cat-feed-placeholder');

    widgets.forEach(widget => {
        const label = widget.getAttribute('data-label');
        if (!label) return;

        // Fetch posts by label
        fetch('/feeds/posts/summary/-/' + label + '?alt=json&max-results=5')
            .then(response => response.json())
            .then(data => {
                const entries = data.feed.entry;
                if (!entries || entries.length === 0) return;

                let html = '<div class="cat-col-title"><h3><a href="/search/label/' + encodeURIComponent(label) + '">' + label + '</a></h3></div>';

                entries.forEach((entry, index) => {
                    const title = entry.title.$t;
                    let link = '#';
                    // Find the alternate link
                    for (let i = 0; i < entry.link.length; i++) {
                        if (entry.link[i].rel === 'alternate') {
                            link = entry.link[i].href;
                            break;
                        }
                    }

                    // Get image
                    let imgUrl = 'https://via.placeholder.com/300x180?text=No+Image';
                    if (entry.media$thumbnail) {
                        // Change size from s72-c to w300-h180-c (approx aspect ratio)
                        imgUrl = entry.media$thumbnail.url.replace(/\/s[0-9]+-c/, "/w300-h180-c"); 
                    }

                    if (index === 0) {
                        // First post prominent
                        html += `
                        <div class="cat-post-item cat-post-first">
                            <a href="${link}"><img src="${imgUrl}" class="post-thumb" alt="${title}"/></a>
                            <h4><a href="${link}">${title}</a></h4>
                        </div>`;
                    } else {
                        // List items
                        html += `
                        <div class="cat-post-item cat-post-list-item">
                            <a href="${link}">${title}</a>
                        </div>`;
                    }
                });

                // Inject the HTML
                const container = widget.closest('.widget-content');
                if (container) {
                    container.innerHTML = html;
                } else {
                    widget.innerHTML = html;
                }
            })
            .catch(error => {
                console.error("Error fetching Label posts:", error);
                widget.innerHTML = "<p>Error loading posts.</p>";
            });
    });


    // Important News Fetch Logic
    const impNewsWidgets = document.querySelectorAll('.important-news-placeholder');
    impNewsWidgets.forEach(widget => {
        const label = widget.getAttribute('data-label') || 'ÿß€ÅŸÖ ÿÆÿ®ÿ±€å⁄∫';

        fetch('/feeds/posts/summary/-/' + label + '?alt=json&max-results=5')
            .then(response => response.json())
            .then(data => {
                const entries = data.feed.entry;
                if (!entries || entries.length === 0) {
                    widget.innerHTML = 'No news found.';
                    return;
                }

                let featuredHtml = '';
                let listHtml = '<div class="imp-list-wrap">';

                entries.forEach((entry, index) => {
                    const title = entry.title.$t;
                    let link = '#';
                    for (let i = 0; i < entry.link.length; i++) {
                       if (entry.link[i].rel === 'alternate') { link = entry.link[i].href; break; }
                    }

                    // Published Date
                    const dateObj = new Date(entry.published.$t);
                    const dateStr = dateObj.toLocaleDateString('ur-PK', { month: 'long', day: 'numeric', year: 'numeric' });

                    // Author
                    let authorName = '';
                    if (entry.author && entry.author.length > 0) {
                        authorName = entry.author[0].name.$t;
                    }

                    // Comments
                    // Note: entry.thr$total exists? usually not in summary unless enabled. will skip or static text.

                    if (index === 0) {
                        // Big Featured Post
                        let imgUrl = 'https://via.placeholder.com/600x400?text=News';
                        if (entry.media$thumbnail) {
                            imgUrl = entry.media$thumbnail.url.replace(/\/s[0-9]+-c/, "/w600-h400-c");
                        }

                        featuredHtml = `
                        <div class="imp-featured-post">
                            <a href="${link}"><img src="${imgUrl}" class="imp-featured-thumb" alt="${title}"/></a>
                            <h3 class="imp-featured-title"><a href="${link}">${title}</a></h3>
                            <div class="imp-featured-meta">
                                <span><i class="far fa-calendar-alt"></i> ${dateStr}</span>
                            </div>
                            <!-- <div class="imp-featured-excerpt">Excerpts or summary if available...</div> -->
                        </div>`;
                    } else {
                        // Small List Posts
                        let imgUrl = 'https://via.placeholder.com/100x80?text=News';
                        if (entry.media$thumbnail) {
                            imgUrl = entry.media$thumbnail.url.replace(/\/s[0-9]+-c/, "/w100-h80-c");
                        }

                        listHtml += `
                        <div class="imp-list-post">
                            <div class="imp-list-content">
                                <h4><a href="${link}">${title}</a></h4>
                                <div class="imp-list-meta">
                                    <span><i class="far fa-calendar-alt"></i> ${dateStr}</span>
                                </div>
                            </div>
                            <a href="${link}"><img src="${imgUrl}" class="imp-list-thumb" alt="${title}"/></a>
                        </div>`;
                    }
                });

                listHtml += '</div>'; // End list wrap

                // Construct Grid: Featured (Right/First in DOM for RTL Grid Col 1?)
                // Wait, if grid-template-columns: 1.5fr 1fr;
                // Col 1 is 1.5fr. Col 2 is 1fr.
                // In RTL: Col 1 is on right.
                // So Featured Post (Col 1) goes first in HTML.

                const finalHtml = `
                <div class="important-news-inner">
                    <div class="imp-featured-wrapper">
                        ${featuredHtml}
                    </div>
                    ${listHtml}
                </div>`;

                widget.innerHTML = finalHtml;
            })
            .catch(err => {
                 console.error("Important News fetch error:", err);
                 widget.innerHTML = "Error loading.";
            });
    });

    // Columns (Aaj ke columns) Fetch Logic
    const colWidgets = document.querySelectorAll('.column-feed-placeholder');
    colWidgets.forEach(widget => {
        const label = widget.getAttribute('data-label') || '⁄©ÿßŸÑŸÖÿ≤'; // Default 'Columns'

        fetch('/feeds/posts/summary/-/' + label + '?alt=json&max-results=8')
            .then(response => response.json())
            .then(data => {
                const entries = data.feed.entry;
                if (!entries || entries.length === 0) {
                    widget.innerHTML = 'No columns found.';
                    return;
                }

                let html = '<div class="columns-widget-container">';
                // Title
                html += '<h3 class="columns-section-title"><a href="/search/label/' + encodeURIComponent(label) + '">ÿ¢ÿ¨ ⁄©€í ⁄©ÿßŸÑŸÖÿ≤</a></h3>';
                html += '<ul class="columns-list">';

                entries.forEach(entry => {
                    const title = entry.title.$t;
                    // Extract Author Name
                    let authorName = '';
                    if (entry.author && entry.author.length > 0) {
                        authorName = entry.author[0].name.$t;
                    }

                    let link = '#';
                    for (let i = 0; i < entry.link.length; i++) {
                       if (entry.link[i].rel === 'alternate') { link = entry.link[i].href; break; }
                    }

                    // Image: Use post thumbnail
                    let imgUrl = 'https://via.placeholder.com/60x60?text=User';
                    if (entry.media$thumbnail) {
                        imgUrl = entry.media$thumbnail.url.replace(/\/s[0-9]+-c/, "/w60-h60-c");
                    }

                    html += `
                    <li class="columns-item">
                        <a href="${link}" style="display:flex; width:100%; align-items:center; text-decoration:none;">
                            <img src="${imgUrl}" class="columns-thumb" alt="${title}"/>
                            <div class="columns-info">
                                <span class="columns-title">${title}</span>
                            </div>
                        </a>
                    </li>`;
                });

                html += '</ul></div>';
                widget.innerHTML = html;
            })
            .catch(err => {
                 console.error("Columns fetch error:", err);
            });
    });

    // Video Section Fetch Logic
    const videoWidgets = document.querySelectorAll('.video-feed-placeholder');
    videoWidgets.forEach(widget => {
        const label = widget.getAttribute('data-label') || 'Videos';

        fetch('/feeds/posts/summary/-/' + label + '?alt=json&max-results=4')
            .then(response => response.json())
            .then(data => {
                const entries = data.feed.entry;
                if (!entries || entries.length === 0) {
                     widget.innerHTML = 'No videos found.';
                     return;
                }

                let html = '<div class="video-header"><h3>Ÿà€å⁄à€åŸàÿ≤</h3></div>';
                html += '<div class="video-grid">';

                entries.forEach(entry => {
                    const title = entry.title.$t;
                    let link = '#';
                    for (let i = 0; i < entry.link.length; i++) {
                       if (entry.link[i].rel === 'alternate') { link = entry.link[i].href; break; }
                    }

                    // High quality thumbnail attempt
                    let imgUrl = 'https://via.placeholder.com/600x340?text=Video';
                    if (entry.media$thumbnail) {
                        imgUrl = entry.media$thumbnail.url.replace(/\/s[0-9]+-c/, "/w600-h340-c"); 
                    }

                    // Published Date
                    const dateObj = new Date(entry.published.$t);
                    const dateStr = dateObj.toLocaleDateString('ur-PK', { month: 'short', day: 'numeric', year: 'numeric' });

                    html += `
                    <div class="video-card">
                        <a href="${link}" title="${title}">
                            <img src="${imgUrl}" class="video-thumb" alt="${title}"/>
                            <span class="video-duration"><i class="far fa-clock"></i> 05:00</span>
                            <div class="play-icon-overlay"><i class="fas fa-play"></i></div>
                            <div class="video-meta-overlay">
                                <span><i class="far fa-calendar-alt"></i> ${dateStr}</span>
